import React, {useState, useEffect} from 'react';
import {Routes, Route, Link, useParams, Outlet, NavLink, useSearchParams} from 'react-router-dom';
import { css, jsx } from '@emotion/react';

function App() {

  return (
    <div>
      <h1>Lé Weather Watcher~</h1>
      <Menu/>
      <Routes>
        <Route path="/" element={<Home/>}/>
        <Route path="/16Day" element={<SixteenDays/>}/>
        <Route path ="/Credits"/>
        <Route path="*" element={<Error/>}/>
      </Routes>
    </div>
  );
}

function Home(){
  return(
    <div>
      <h2>
        Welcome to Lé Weather Watcher. Please select from the above menu options!
      </h2>
    </div>
  );

}

function Menu(){

    return (
      <nav>
        <NavLink to='/'>Home</NavLink>
        <NavLink to='/16Day'>16 Days Forecast</NavLink>
        <NavLink to='/Credits'>Credits</NavLink>
      </nav>
    );
}

function SixteenDays(){

  const [weather, setWeather] = useState([]);
  const [Latitude, setLatitude] = useState([]);
  const [Longtitude, setLongtitude] = useState([]);
  const [cnt, setCount] = useState('');
  const [cityName, setCity] = useState('');
  const [stateCode, setStateCode] = useState('');
  const [countryCode, setCountryCode] = useState('');


  const getDataRequest = async(Latitude, Longtitude, cnt)=>{
    const url = `https://api.openweathermap.org/data/2.5/forecast/daily?lat=${Latitude}&lon=${Longtitude}&cnt=${cnt}&units=metric&appid=da655239f538023532cc87591851330d`
    const response = await fetch(url)
    const responseJSON = await response.json()

    setWeather(responseJSON.list)
  }

  const getGeoCodeRequest = async(cityName, stateCode, countryCode)=>{
    const geocode = `http://api.openweathermap.org/geo/1.0/direct?q=${cityName},${stateCode},${countryCode}&limit=5&appid={da655239f538023532cc87591851330d}`
    const responseGeo = await fetch(geocode)
    const responseJsonGeo = await responseGeo.json()
    console.log(responseJsonGeo)

    if(responseJsonGeo.lat && responseJsonGeo.lon){
      setLatitude(responseJsonGeo.lat)
      setLongtitude(responseJsonGeo.lon)
    }
  }
  useEffect(()=>{
    getGeoCodeRequest(cityName, stateCode, countryCode);
  }, [cityName, stateCode, countryCode])

  useEffect(()=>{
    getDataRequest(Latitude, Longtitude);
  }, [Latitude, Longtitude])

  return(
   <div>
      <Search cityName={cityName} setCityName={setCity} stateCode={stateCode} setStateCode={setStateCode} countryCode={countryCode} setCountryCode={setCountryCode} cnt={cnt} setCount={setCount}/>
      <Display data = {weather}/>
    </div>


  )

}

const Search=(props)=>{
  console.log(props)
  return(
    <form 
        onSubmit={(e)=>{e.preventDefault();
        setCity({q:cityName})
      }}>
      <fieldset>
        <input value = {cityName} onChange={e=>setCityName(e.target.value)}>Enter City Name Here</input>
        <input value = {stateCode} onChange={e=>setStateCode(e.target.value)}>Enter State Code Here</input>
        <input value = {countryCode} onChange={e=>setCountryCode(e.target.value)}>Enter Country Code Here</input>
        <button type = "Submit">Search</button>
      </fieldset>
      </form>
  )

}

const Display = (props) =>{


  return(
    <div>
      {props.data.map((index, variable)=>  
          <div key = {variable}>
            <h1>Day {variable+1}</h1>
            <p>Time: {Date(index.dt)}</p>
            <p>High: {index.temp.max} Celcius</p>
            <p>Low: {index.temp.min} Celcius</p>
            <p>Possibility of Precipitation: {index.pop * 100}%</p>
            <div>
              <Image  icon= {index.weather}/>
            </div>
            <p>Description: {
              index.weather.map((number, key)=><div key={key}>{number.description}</div>)
            }</p>
          </div>
      )}
    </div>
  )
}

const Image = (props)=>{
  console.log(props)
  return(
    <div>
      {props.icon.map((variable, index)=>
        <img key ={index} src = {`http://openweathermap.org/img/wn/${variable.icon}@2x.png`}></img>
      )}

    </div>
  )

}

function History(){



}

function Credits(){}

function Error(){
  return(
    <div>
      This data entry does not exist, please hit back
    </div>
  )

}

export default App;
