import React, {useState, useEffect} from 'react';
import {Routes, Route, Link, useParams, Outlet, NavLink, useSearchParams} from 'react-router-dom';
import { css, jsx } from '@emotion/react';

const API = {
  key: "da655239f538023532cc87591851330d",
  base: "https://api.openweathermap.org/data/2.5/forecast/"
}

function App() {
  //const [searchParams] = useSearchParams();

  return (
    <div>
      <h1>Lé Weather Watcher~</h1>
      <Menu/>
      <Routes>
        <Route path="/" element={<Home/>}/>
        <Route path="/16Day" element={<SixteenDays/>}/>
        <Route path ="/Credits"/>
        <Route path="*"/>
      </Routes>
    </div>
  );
}

function Home(){
  return(
    <div>
      <h2>
        Welcome to Lé Weather Watcher. Please select from the above menu options!
      </h2>
    </div>
  );

}

function Menu(){

    return (
      <nav>
        <NavLink to='/'>Home</NavLink>
        <NavLink to='/16Day'>16 Days Forecast</NavLink>
        <NavLink to='/Credits'>Credits</NavLink>
      </nav>
    );
}

function SixteenDays(){

  const [weather, setWeather] = useState([]);
  const [date, setDate] = useState([]);
  const [Latitutde, setLatitude] = useState([]);
  const [longtitude, setLongtitude] = useState([]);



  const getDataRequest = async()=>{
    const url = 'https://api.openweathermap.org/data/2.5/forecast/daily?lat=35&lon=139&cnt=5&units=metric&appid=da655239f538023532cc87591851330d'
    const response = await fetch(url)
    const responseJSON = await response.json()
    console.log(responseJSON)
    setWeather(responseJSON.list)
    setDate(responseJSON.city)


  }

  /*const getGeoCodeRequest = async()=>{
    const geocode = `http://api.openweathermap.org/geo/1.0/direct?q=${cityName},${stateCode},${countryCode}&limit=${limit}&appid={da655239f538023532cc87591851330d}`
    const responseGeo = await fetch(geocode)
    const responseJsonGeo = await responseGeo.json()
    console.log(responseJsonGeo)
    setLatitude(responseJsonGeo)
  }
  useEffect(()=>{
    getGeoCodeRequest();
  }, [])
  */
  useEffect(()=>{
    getDataRequest();
  }, [])

  return(/*
   <div>
      <form 
        onSubmit={(e)=>{e.preventDefault();
        setSearchParams({q:searchParams})
      }}>
      <fieldset>
        <input value = {cityName} onChange={e=>setCityName(e.target.value)}>Enter City Name Here</input>
        <input value = {stateCode} onChange={e=>setStateCode(e.target.value)}>Enter State Code Here</input>
        <input value = {countryCode} onChange={e=>setCountryCode(e.target.value)}>Enter Country Code Here</input>
        <button type = "Submit">Search</button>
      </fieldset>
      </form>
    </div>*/
    <div>
      <Display data={weather}/>
    </div>

  )

}

const Display = (props) =>{
  console.log(props);

  return(
    <div>
      {props.data.map((index, variable)=>  
          <div key = {variable}>
            <h1>Day {variable+1}</h1>
            <p>Time: {Date(index.dt)}</p>
            <p>High: {index.temp.max} Celcius</p>
            <p>Low: {index.temp.min} Celcius</p>
            <p>Possibility of Precipitation: {index.pop * 100}%</p>
            <img src='http://openweathermap.org/img/wn/$10d@2x.png' alt='weather'>

            </img>
            <p>Description: {
              index.weather.map((number, key)=><div key={key}>{number.description}</div>)
            }</p>
          </div>
      )}
    </div>
  )

}

