/** @jsxImportSource @emotion/react */

import React, {useState, useEffect} from 'react';
import {Routes, Route, NavLink} from 'react-router-dom';
import { Global, css, jsx } from '@emotion/react';

const globalStyle = css`
  @import url('https://fonts.googleapis.com/css2?family=Redressed&display=swap');
    body{
      font-family: 'Redressed', cursive;
      margin: 0;
    }
`

function App() {


  const headerStyle = css`
    background-color: deepskyblue;
    color: ghostwhite;
    justify-content: center;
    padding: 10px;
    border: 15px groove lightskyblue;
    display: flex;

  `
  const divStyle =css`
    background-image:linear-gradient(snow, lightskyblue 30%, skyblue 20%,deepskyblue);
    background-size: cover;
    height: 400vh;
    width: 98vw;
  `

  return (
    <div css={divStyle}>
      <h1 css={headerStyle}>Lé Weather Watcher~</h1>
      <Menu/>
      <Routes>
        <Route path="/" element={<Home/>}/>
        <Route path="/16Day" element={<SixteenDays/>}/>
        <Route path ="/Credits" element={<Credits/>}/>
        <Route path="*" element={<Error/>}/>
      </Routes>
    </div>
  );
}

function Home(){

  const textStyle=css`
    text-align: center;
    color: royalblue;
    padding-top: 10%

  `
  return(
    <div>
      <h2 css={textStyle}>
        Welcome to Lé Weather Watcher. Please select from the above menu options!
      </h2>
    </div>
  );

}

function Menu(){

  const menuStyle =css`
    background-color: skyblue;
    color: snow;
    display: flex;
    justify-content: space-evenly;
    padding: 20px;
    border: 20px groove snow;
    `
  const linkStyle = css`
    color: snow;
    text-decoration: none;
    font-size: 20px;
    font-style: bold;
  `
    return (
      <nav css={menuStyle}>
        <NavLink css={linkStyle} to='/'>Home</NavLink>
        <NavLink css={linkStyle} to='/16Day'>16 Days Forecast</NavLink>
        <NavLink css={linkStyle} to='/Credits'>Credits</NavLink>
      </nav>
    );
}

function SixteenDays(){

  const [weather, setWeather] = useState([]);
  const [cnt, setCount] = useState('');
  const [cityName, setCity] = useState('');
  const [stateCode, setStateCode] = useState('');
  const [countryCode, setCountryCode] = useState('');
  const [state, setState] = useState(false)

  const getDataRequest = async(cityName, stateCode, countryCode, cnt)=>{
    const url = `https://api.openweathermap.org/data/2.5/forecast/daily?q=${cityName},${stateCode},${countryCode}&cnt=${cnt}&units=metric&appid=da655239f538023532cc87591851330d`
    const response = await fetch(url)
    const responseJSON = await response.json()
    if(responseJSON.list){ 
      setWeather(responseJSON.list)
    }
  }
  useEffect(()=>{
    getDataRequest(cityName,stateCode,countryCode, cnt);
  }, [cityName, stateCode, countryCode, cnt])

  return(
   <div>
      <Search cityName={cityName} setCity={setCity} stateCode={stateCode} setStateCode={setStateCode} countryCode={countryCode} 
        setCountryCode={setCountryCode} cnt={cnt} setCount={setCount} getDataRequest={getDataRequest} setState={setState} state={state}/>
      <div>
        {state ? <Display data = {weather}/> : null }
      </div>
    </div>


  )

}

const Search=(props)=>{

  const formStyle = css`
    background-color: snow;
    display: flex;
    justify-content: center; 
    border: 10px ridge lightskyblue;
    border-bottom-style: 10px groove lightskyblue;

  `

  return(
    <form 
        onSubmit={(e)=>{e.preventDefault();
        props.setCity(props.cityName)
        props.setStateCode(props.stateCode)
        props.setCountryCode(props.countryCode)
        props.setCount(props.cnt)
        props.getDataRequest(props.cityName, props.stateCode, props.countryCode, props.cnt)
        props.setState(true)
      }}>
      <fieldset css={formStyle}>
        <input size="30" value = {props.cityName} onChange={(e)=>props.setCity(e.target.value)} placeholder='Enter City Name Here'></input>
        <input size="30" value = {props.stateCode} onChange={(e)=>props.setStateCode(e.target.value)} placeholder='Enter State Code Here<'></input>
        <input size="30" value = {props.countryCode} onChange={(e)=>props.setCountryCode(e.target.value)} placeholder='Enter Country Code Here'></input>
        <input size="30" value = {props.cnt} onChange={(e)=>props.setCount(e.target.value)} placeholder='Enter No. of Days (1-16)'></input>
        <button type = "Submit">Search</button>
      </fieldset>
      </form>
  )

}

const Display = (props) =>{


  return(
    <div>
      {props.data.map((index, variable)=>  
          <div key = {variable}>
            <h1>Day {variable+1}</h1>
            <p>Time: {Date(index.dt)}</p>
            <p>High: {index.temp.max} Celcius</p>
            <p>Low: {index.temp.min} Celcius</p>
            <p>Possibility of Precipitation: {index.pop * 100}%</p>
            <div>
              <Image  icon= {index.weather}/>
            </div>
            <p>Description: {
              index.weather.map((number, key)=><div key={key}>{number.description}</div>)
            }</p>
          </div>
      )}
    </div>
  )
}

const Image = (props)=>{
  return(
    <div>
      {props.icon.map((variable, index)=>
        <img key ={index} src = {`http://openweathermap.org/img/wn/${variable.icon}@2x.png`} alt ="weather"></img>
      )}

    </div>
  )

}

function History(){



}

function Credits(){

  const textStyle=css`
    text-align: center;
    color: royalblue;
    padding-top: 10%;
    font-size: 20px;

    a{
      color: mediumblue;
    }
  `
  return(
    <div css={textStyle}>
      <p>This website uses the OpenWeather API. For more information, visit their website at: </p>
      <a href="https://openweathermap.org">https://openweathermap.org</a>
    </div>
  )
}

function Error(){
  return(
    <div>
      This data entry does not exist, please hit back
    </div>
  )

}

export default App;
